import React, { useState, useEffect, Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import ArticlesCards from "./Articles_cards.js";

const getTags = async (callback) => {
  const result = await fetch("/tags.json").then((res) => res.json());

  let keysArray = Object.keys(result);

  keysArray.map(() => {
    return false
  })

  callback([...keysArray]);
};


const TagButtons = ({ tag, index }) => {
  const [buttonState, updateButtonState] = useState(false);

  const changeState = () => {
    updateButtonState(!buttonState);
  };

  useEffect(() => {
    updateButtonState(buttonState);
  }, []);

  return buttonState ? (
    <button
      type="button"
      name={"button:" + tag}
      onClick={() => {
        changeState();
      }}
      class="active btn-primary rounded "
    >
      {tag}
    </button>
  ) : (
    <button
      type="button"
      name={"button:" + tag}
      onClick={() => {
        changeState();
      }}
      class="rounded"
    >
      {tag}
    </button>
  );
};

const ArticleTags = ({ articleURL }) => {
  const [articleState, updateArticleState] = useState(["tags loading..."]);
  const [activatedTags, setActivatedTags] = useState([])

  // Retrieves the list of items from the Express app
  useEffect(() => {
    getTags(updateArticleState, setActivatedTags);
  }, [articleState]);

  return (
    <>
      <div class="row">
        <div class="col-12 mb-3 mt-2 rounded justify-content-around" data-toggle="buttons-radio" aria-label="Article Tags">
          {articleState.map((tag, index) => (
            <TagButtons tag={tag} index={index} />
          ))}
        </div>
      </div>
      <ArticlesCards articleURL={articleURL} activeTags={articleState} />
    </>
  );
};

export default ArticleTags;
