import React, { useState, useEffect, Component } from "react";
import Link from "next/link"
import { useRouter } from "next/router";
import ReactHtmlParser from "react-html-parser";
import showdown from "showdown";

const converter = new showdown.Converter();

const getArticle = (url) => {
  return fetch("/api/articles/"+url)
    .then(res => {
      return res.text()
    })
    .then(res => {
      console.log({res});
      return res
    })
    .catch((err) => {
      console.log(err);
    })
}

const articleCard = (articleText) => {
  return (
    <div class="card">
      <div class="card-body">
        <h5 class="card-title">{articleText}</h5>
        {articleText.includes("html") ? (
          <p class="card-text">{articleText.slice(0, 200)}</p>
        ) : (
          <p class="card-text">{articleText.slice(0, 200)}</p>
        )}
      </div>
    </div>
  );
};

const ArticlesCard = ({ articleURL }) => {
  const location = useRouter();
  const [articleState, updateArticleState] = useState(["articles loading..."]);

  // Retrieves the list of items from the Express app
  useEffect(() => {
    Promise.all(articleURL.map(getArticle)).then((proRes) => {
      updateArticleState(proRes);
    });
  }, [articleURL]);

  return (
    <div class="row">
      <div class="col-12 rounded justify-content-around" data-toggle="buttons-radio" aria-label="Article Tags">
        {articleState.map(articleCard)}
      </div>
    </div>
  );
};

export default ArticlesCard;
