import React, { useState, useEffect, Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import ReactHtmlParser from "react-html-parser";
import ArticleHome from "./Articles_home.js";
import { useRouter } from "next/router";
// import MathJax from "@matejmazur/react-mathjax";

const Articles = () => {
  const location = useRouter()
  console.log(process.env);
  console.log("HERE : ", process.env.URL + location.pathname);

  const [listState, updateListState] = useState([]);

  // Retrieves the list of items from the Express app
  const getList = () => {
    fetch("/")
    .then(result => {console.log("plop here is : ", result)})
      // .then((res) => res.json())
      // .then((list) => updateListState(list));
  };

  useEffect(() => {
    getList();
  }, []);

  return (
    <div className="App">
      {location.pathname.length < 10 ? (
        <div>
          <ul>
            <ArticleHome />
          </ul>
        </div>
      ) : (
        <div></div>
      )}

      {/* <Route path={`${process.env.URL + location.pathname}/:articleId`} component={Article} /> */}
    </div>
  );
};

const Article = () => {
  const [articleState, updateArticleState] = useState();

  const match = {
    params: {
      articleId: 5
    }
  }

  // Retrieves the list of items from the Express app
  let title = "Loading...";
  const getArticle = () => {
    fetch("/api/getArticle/" + match.params.articleId)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        title = match.params.articleId;
        updateArticleState(data[1]);
      });
  };

  useEffect(() => {
    getArticle();
  }, [articleState, title, match.params.articleId]);

  getArticle();

  return (
    <div>
      {articleState ? (
        <div>
          {/* <div dangerouslySetInnerHTML={{ __html: articleState }} /> */}
          {ReactHtmlParser(articleState)}
        </div>
      ) : (
        <div></div>
      )}
    </div>
  );
};

export default Articles;
