  }, [articleState]);

  return (
    <div class="row">
      <div class="col-12 rounded justify-content-around" data-toggle="buttons-radio" aria-label="Article Tags">
        {articleURL.map((url) => {
          return articleCard(url)
        })}
      </div>
    </div>
  );
};

export default ArticlesCard;
